def on_on_overlap(sprite3, otherSprite3):
    global points
    points += -1
    info.change_score_by(-1)
    if 1 > points:
        game.over(False)
    pause(2000)
sprites.on_overlap(SpriteKind.player, SpriteKind.projectile, on_on_overlap)

